import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [year, setYear] = useState("");
  const [class_, setClass] = useState("");
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/list").then((res) => {
      setStudentList(res.data);
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/add", {
      firstName: firstName,
      lastName: lastName,
      year: year,
      class: class_,
    }).then((r) => {
      setStudentList([
        ...studentList,
        {
          firstName: firstName,
          lastName: lastName,
          year: year,
          class: class_,
          id: r.data,
        },
      ]);
    });
  };

  const deleteReview = (id) => {
    Axios.post("http://localhost:3001/delete", { id });
    setStudentList(studentList.filter((student) => student.id !== id));
  };

  const updateReview = (v) => {
    console.log(v);
    /* Axios.post("http://localhost:3001/update",  {
      firstName: firstName,
      lastName: lastName,
      year: year,
      class: class_,
      id: r.data,
    },);*/
    //setStudentList(studentList.filter((student) => student.id !== id));
  };

  return (
    <>
      <div className="App">
        <h1> Students </h1>
        <div className="wrapper">
          {/*--------------------------------------------------------------------*/}
          <div className="form">
            <label> First Name: </label>
            <input
              type="text"
              name="movieName"
              onChange={(e) => {
                setFirstName(e.target.value);
              }}
            ></input>
            <label> Last name: </label>
            <input
              type="text"
              name="review"
              onChange={(e) => {
                setLastName(e.target.value);
              }}
            ></input>
            <label> Year: </label>
            <input
              type="text"
              name="review"
              onChange={(e) => {
                setYear(e.target.value);
              }}
            ></input>
            <label> Class: </label>
            <input
              type="text"
              name="review"
              onChange={(e) => {
                setClass(e.target.value);
              }}
            ></input>{" "}
            <button onClick={submitReview}> Submit </button>
          </div>
          {/*--------------------------------------------------------------------*/}
          {studentList.map((v) => {
            return (
              <Card
                firstName={v.firstName}
                lastName={v.lastName}
                year={v.year}
                class={v.class}
              ></Card>
            );
          })}
          {/*--------------------------------------------------------------------*/}
        </div>
      </div>
    </>
  );
}

class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isEdit: false,
      firstName: props.firstName,
      lastName: props.lastName,
      year: props.year,
      class_: props.class,
    };
  }

  render() {
    return (
      <div className="card">
        {false ? (
          <>
            <p>First name:{this.state.firstName}</p>
            <p>Last name:{this.state.lastName}</p>
            <p>Year: {this.state.year}</p>
            <p>Class: {this.state.class_}</p>
          </>
        ) : (
          <>
            <p>
              First name:
              <input type="text" value={props.firstName}></input>
            </p>
            <p>Last name:{props.lastName}</p>
            <p>Year: {props.year}</p>
            <p>Class: {props.class}</p>
          </>
        )}
        <button onClick={() => deleteReview(v.id)}>Delete</button>
        <button onClick={() => updateReview()}>Update</button>
      </div>
    );
  }
}

export default App;
