import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [year, setYear] = useState("");
  const [class_, setClass] = useState("");
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/list").then((res) => {
      setStudentList(res.data);
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/add", {
      firstName: firstName,
      lastName: lastName,
      year: year,
      class: class_,
    });
    setStudentList([
      ...studentList,
      {
        firstName: firstName,
        lastName: lastName,
        year: year,
        class: class_,
      },
    ]);
  };

  /*const deleteReview = () => {
    Axios.delete("http://localhost:3001/delete", movieName);
  };*/

  return (
    <div className="App">
      <h1> Studenti </h1>
      <div className="form">
        <label> First Name </label>
        <input
          type="text"
          name="movieName"
          onChange={(e) => {
            setFirstName(e.target.value);
          }}
        ></input>
        <label> Last name </label>
        <input
          type="text"
          name="review"
          onChange={(e) => {
            setLastName(e.target.value);
          }}
        ></input>
        <label> Year </label>
        <input
          type="text"
          name="review"
          onChange={(e) => {
            setYear(e.target.value);
          }}
        ></input>
        <label> Class </label>
        <input
          type="text"
          name="review"
          onChange={(e) => {
            setClass(e.target.value);
          }}
        ></input>
        <button onClick={submitReview}> Submit </button>
        {studentList.map((v) => {
          return (
            <div className="card">
              <h3>
                Name: {v.firstName} {v.lastName}
              </h3>
              <p>Year: {v.year}</p>
              <p>Class: {v.class}</p>
              <button>Delete</button>
              <button onClic>Update</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
