import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [student, setStudent] = useState({
    firstName: "",
    lastName: "",
    year: "",
    class: "",
    id: "",
  });
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:8080/list").then(({ data }) => {
      setStudentList(data);
    });
  }, []);

  const submitStudent = () => {
    Axios.post("http://localhost:8080/add", {
      firstName: student.firstName,
      lastName: student.lastName,
      year: student.year,
      class: student.class,
    }).then(({ data }) => {
      setStudentList([
        ...studentList,
        {
          firstName: student.firstName,
          lastName: student.lastName,
          year: student.year,
          class: student.class,
          id: data,
        },
      ]);
    });
  };

  const deleteStudent = (id) => {
    Axios.post("http://localhost:8080/delete", {
      id,
    });
    setStudentList(studentList.filter((student) => student.id !== id));
  };

  const updateStudent = (v) => {
    // UI
    v.edit = true;
    setStudentList([...studentList]);
  };

  const uploadChange = (v) => {
    v.edit = false;
    setStudentList([...studentList]);
    Axios.post("http://localhost:8080/update", {
      firstName: v.firstName,
      lastName: v.lastName,
      year: v.year,
      class: v.class, // cannot destructure object because "class" 
      id: v.id,
    });
  };

  const manageInputs = ({ name, value }) => {
    setStudent({ ...student, [name]: value });
  };

  return (
    <div className="App">
      <h1> Students </h1>
      <div className="wrapper">
        <div className="form">
          <label> First Name: </label>
          <input
            id="firstNameInput"
            type="text"
            name="firstName"
            onChange={(e) => {
              manageInputs(e.target);
            }}
          ></input>
          <label> Last name: </label>
          <input
            id="lastNameInput"
            type="text"
            name="lastName"
            onChange={(e) => {
              manageInputs(e.target);
            }}
          ></input>
          <label> Year: </label>
          <input
            id="yearInput"
            type="text"
            name="year"
            onChange={(e) => {
              manageInputs(e.target);
            }}
          ></input>
          <label> Class: </label>
          <input
            id="classInput"
            type="text"
            name="class"
            onChange={(e) => {
              manageInputs(e.target);
            }}
          ></input>
          <button onClick={submitStudent}> Submit </button>
        </div>
        {/*--------------------------------------------------------------------*/}
        {studentList.map((v) => {
          return (
            <div key={v.id} className="card">
              {!v.edit ? (
                <>
                  <p> First name: {v.firstName} </p>
                  <p> Last name: {v.lastName} </p> <p> Year: {v.year} </p>
                  <p> Class: {v.class} </p>
                  <button onClick={() => deleteStudent(v.id)}> Delete </button>
                  <button onClick={() => updateStudent(v)}> Update </button>
                </>
              ) : (
                <>
                  <p>
                    First name:
                    <input
                      type="text"
                      placeholder={v.firstName}
                      onChange={(e) => {
                        v.firstName = e.target.value;
                      }}
                    ></input>
                  </p>
                  <p>
                    Last name:
                    <input
                      type="text"
                      placeholder={v.lastName}
                      onChange={(e) => {
                        v.lastName = e.target.value;
                      }}
                    ></input>
                  </p>
                  <p>
                    Year:
                    <input
                      type="text"
                      placeholder={v.year}
                      onChange={(e) => {
                        v.year = e.target.value;
                      }}
                    ></input>
                  </p>
                  <p>
                    Class:
                    <input
                      type="text"
                      placeholder={v.class}
                      onChange={(e) => {
                        v.class = e.target.value;
                      }}
                    ></input>
                  </p>
                  <button onClick={() => uploadChange(v)}> Submit </button>
                </>
              )}
            </div>
          );
        })}
        {/*--------------------------------------------------------------------*/}
      </div>
    </div>
  );
}

export default App;
