import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Card } from "./components.js";

function App() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [year, setYear] = useState("");
  const [class_, setClass] = useState("");
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/list").then((res) => {
      setStudentList(res.data);
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/add", {
      firstName: firstName,
      lastName: lastName,
      year: year,
      class: class_,
    }).then((r) => {
      setStudentList([
        ...studentList,
        {
          firstName: firstName,
          lastName: lastName,
          year: year,
          class: class_,
          id: r.data,
        },
      ]);
    });
  };

  const deleteReview = (id) => {
    Axios.post("http://localhost:3001/delete", { id });
    setStudentList(studentList.filter((student) => student.id !== id));
  };

  const updateReview = (v) => {
    console.log(v);
    /* Axios.post("http://localhost:3001/update",  {
      firstName: firstName,
      lastName: lastName,
      year: year,
      class: class_,
      id: r.data,
    },);*/
    //setStudentList(studentList.filter((student) => student.id !== id));
  };

  return (
    <div className="App">
      <h1> Students </h1>
      <div className="wrapper">
        <div className="form">
          <label> First Name: </label>
          <input
            type="text"
            name="movieName"
            onChange={(e) => {
              setFirstName(e.target.value);
            }}
          ></input>
          <label> Last name: </label>
          <input
            type="text"
            name="review"
            onChange={(e) => {
              setLastName(e.target.value);
            }}
          ></input>
          <label> Year: </label>
          <input
            type="text"
            name="review"
            onChange={(e) => {
              setYear(e.target.value);
            }}
          ></input>
          <label> Class: </label>
          <input
            type="text"
            name="review"
            onChange={(e) => {
              setClass(e.target.value);
            }}
          ></input>
          <button onClick={submitReview}> Submit </button>
        </div>
        {studentList.map((v) => {
          return (
            <Card
              firstName={v.firstName}
              lastName={v.lastName}
              year={v.year}
              class={v.class}
            ></Card>
          );
       /*   <div className="card">
            <p>
              First name:
              <b>{v.firstName}</b>
            </p>
            <p>
              Last name:
              <b>{v.lastName}</b>
            </p>
            <p>
              Year: <b>{v.year}</b>
            </p>
            <p>
              Class: <b>{v.class}</b>
            </p>
            <button onClick={() => deleteReview(v.id)}>Delete</button>
            <button onClick={() => updateReview()}>Update</button>
          </div>;*/
        })}
      </div>
    </div>
  );
}

export default App;
