import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [movieName, setMovieName] = useState("");
  const [review, setReview] = useState("");
  const [movieReviewList, setmovieReviewList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/list").then((res) => {
      console.log(res.data);
      setmovieReviewList(res.data);
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/add", {
      movieName: movieName,
      movieReview: review,
    });
    setmovieReviewList([
      ...movieReviewList,
      {
        movieName: movieName,
        movieReview: review,
      },
    ]);
  };

  const deleteReview = () => {
    Axios.delete("http://localhost:3001/delete", movieName);
  };

  return (
    <div className="App">
      <h1> CRUD </h1>
      <div className="form">
        <label> First Name </label>
        <input
          type="text"
          name="movieName"
          onChange={(e) => {
            setMovieName(e.target.value);
          }}
        ></input>
        <label> LastName </label>
        <input
          type="text"
          name="review"
          onChange={(e) => {
            setReview(e.target.value);
          }}
        ></input>
        <button onClick={submitReview}> Submit </button>
        {movieReviewList.map((v) => {
          console.log(v);
          return (
            <div className="card">
              <h3>Name: {v.firstName} </h3>
              <p>Year: {v.year}</p>
              <p>Class: {v.class}</p>
              <button>Delete</button>
              <button>Update</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
