import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [movieName, setMovieName] = useState("");
  const [review, setReview] = useState("");
  const [movieReviewList, setmovieReviewList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/api/list").then((res) => {
      console.log(res.data);
      setmovieReviewList(res.data);
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/api/insert", {
      movieName: movieName,
      movieReview: review,
    });
    console.log(...movieReviewList, {
      movieName: movieName,
      movieReview: review,
    });
    setmovieReviewList([...movieReviewList, {
      movieName: movieName,
      movieReview: review,
    }]);
  };

  return (
    <div className="App">
      <h1> CRUD </h1>
      <div className="form">
        <label> Movie Name </label>
        <input
          type="text"
          name="movieName"
          onChange={(e) => {
            setMovieName(e.target.value);
          }}
        ></input>
        <label> Review </label>
        <input
          type="text"
          name="review"
          onChange={(e) => {
            setReview(e.target.value);
          }}
        ></input>
        <button onClick={submitReview}> Submit </button>
        {movieReviewList.map((v) => {
          console.log(v);
          return (
            <h2>
              name: {v.movieName} | review: {v.movieReview}
            </h2>
          );
        })}
      </div>
    </div>
  );
}

export default App;
